import { DodoPayments } from 'dodopayments'

let dodopaymentsClient: DodoPayments | null = null

export function getDodoPaymentsClient(): DodoPayments {
  if (!dodopaymentsClient) {
    const token = process.env.DODO_PAYMENTS_API_KEY
    const environment = process.env.DODO_PAYMENTS_ENVIRONMENT as "live_mode" | "test_mode"

    if (!token) {
      throw new Error(`
        DODO_PAYMENTS_API_KEY environment variable is missing.

        Please check:
        1. Your .env file exists in the project root
        2. The file contains: DODO_PAYMENTS_API_KEY=<your-api-key>
        3. You've restarted your development server
        4. No extra quotes or spaces in the .env file
      `)
    }

    if (!environment || (environment !== "live_mode" && environment !== "test_mode")) {
      throw new Error('DODO_PAYMENTS_ENVIRONMENT must be either "live_mode" or "test_mode"')
    }

    dodopaymentsClient = new DodoPayments({
      bearerToken: token,
      environment: environment,
    })
  }

  return dodopaymentsClient
}

import express from 'express';
import { checkoutRouter } from './checkout';
import { customerRouter } from './customer';
import { paymentsRouter } from './payments';
import { productsRouter } from './products';
import { subscriptionsRouter } from './subscriptions';
import { webhookRouter } from './webhook';

const router = express.Router();

router.use('/checkout', checkoutRouter);
router.use('/customer', customerRouter);
router.use('/payments', paymentsRouter);
router.use('/products', productsRouter);
router.use('/subscriptions', subscriptionsRouter);
router.use('/webhook', webhookRouter);

export { router as dodopaymentsRouter };

import express from 'express';
import { getDodoPaymentsClient } from '../../lib/dodopayments';
import { DodoPayments } from 'dodopayments';
import { z } from 'zod';

const router = express.Router();

const productCartItemSchema = z.object({
    product_id: z.string().min(1, "Product ID is required"),
    quantity: z.number().int().min(1, "Quantity must be at least 1"),
    amount: z.number().int().min(0).optional(),
});

const attachExistingCustomerSchema = z.object({
    customer_id: z.string().min(1, "Customer ID is required"),
});

const newCustomerSchema = z.object({
    email: z.string().email("Invalid email format"),
    name: z.string().min(1, "Name is required"),
    phone_number: z.string().optional().nullable(),
    create_new_customer: z.boolean().optional(),
});

const customerSchema = z.union([attachExistingCustomerSchema, newCustomerSchema]);

const billingAddressSchema = z.object({
    city: z.string().min(1, "City is required"),
    country: z.string().regex(/^[A-Z]{2}$/, "Country must be a 2-letter uppercase ISO code"),
    state: z.string().min(1, "State is required"),
    street: z.string().min(1, "Street address is required"),
    zipcode: z.string().min(1, "Zipcode is required"),
});

const checkoutSessionSchema = z.object({
    productCart: z.array(productCartItemSchema).min(1, "At least one product is required"),
    customer: customerSchema,
    billing_address: billingAddressSchema,
    return_url: z.string().url("Return URL must be a valid URL"),
    customMetadata: z.record(z.string(), z.string()).optional(),
});

router.post('/', async (req, res) => {
    try {
        const validationResult = checkoutSessionSchema.safeParse(req.body);
        if (!validationResult.success) {
            return res.status(400).json({
                error: "Validation failed",
                details: validationResult.error.issues.map(issue => ({
                    field: issue.path.join('.'),
                    message: issue.message
                }))
            });
        }

        const { productCart, customer, billing_address, return_url, customMetadata } = validationResult.data;

        const session = await getDodoPaymentsClient().checkoutSessions.create({
            product_cart: productCart,
            customer: customer,
            billing_address: billing_address as DodoPayments.Payments.BillingAddress,
            return_url: return_url,
            metadata: customMetadata,
        });

        res.json(session);
    } catch (error) {
        console.error('Error in checkout POST handler:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

export { router as checkoutRouter };

import express from 'express';
import { getDodoPaymentsClient } from '../../lib/dodopayments';
import { z } from 'zod';

const router = express.Router();

const customerCreateSchema = z.object({
    email: z.string().email("Invalid email format"),
    name: z.string().min(1, "Name is required"),
    phone_number: z.string().optional().nullable(),
});

const customerUpdateSchema = z.object({
    email: z.string().email("Invalid email format").optional(),
    name: z.string().min(1, "Name is required").optional(),
    phone_number: z.string().optional().nullable(),
});

router.get('/', async (req, res) => {
    try {
        const { customer_id } = req.query;

        if (!customer_id || typeof customer_id !== 'string') {
            return res.status(400).json({ error: 'customer_id is required' });
        }

        const customer = await getDodoPaymentsClient().customers.retrieve(customer_id);
        res.json(customer);
    } catch (error) {
        console.error('Error fetching customer:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

router.post('/', async (req, res) => {
    try {
        const validationResult = customerCreateSchema.safeParse(req.body);
        if (!validationResult.success) {
            return res.status(400).json({
                error: "Validation failed",
                details: validationResult.error.issues.map(issue => ({
                    field: issue.path.join('.'),
                    message: issue.message
                }))
            });
        }

        const customer = await getDodoPaymentsClient().customers.create(validationResult.data);
        res.json(customer);
    } catch (error) {
        console.error('Error creating customer:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

router.put('/', async (req, res) => {
    try {
        const { customer_id } = req.query;

        if (!customer_id || typeof customer_id !== 'string') {
            return res.status(400).json({ error: 'customer_id is required' });
        }

        const validationResult = customerUpdateSchema.safeParse(req.body);
        if (!validationResult.success) {
            return res.status(400).json({
                error: "Validation failed",
                details: validationResult.error.issues.map(issue => ({
                    field: issue.path.join('.'),
                    message: issue.message
                }))
            });
        }

        const customer = await getDodoPaymentsClient().customers.update(customer_id, validationResult.data);
        res.json(customer);
    } catch (error) {
        console.error('Error updating customer:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

router.get('/subscriptions', async (req, res) => {
    try {
        const { customer_id } = req.query;

        if (!customer_id || typeof customer_id !== 'string') {
            return res.status(400).json({ error: 'customer_id is required' });
        }

        const subscriptions = await getDodoPaymentsClient().subscriptions.list({
            customer_id: customer_id
        });
        res.json(subscriptions);
    } catch (error) {
        console.error('Error fetching customer subscriptions:', error);
        res.status(500).json({ error: 'Internal server error' });
    }
});

