export const checkout = async ({ baseUrl, productCart, customer, billing_address, return_url, customMetadata }: { baseUrl?: string, productCart: Array<{ product_id: string; quantity: number; amount?: number }>, customer: DodoPayments.Payments.CustomerRequest, billing_address: DodoPayments.Payments.BillingAddress, return_url: string, customMetadata?: Record<string, string> }) => {
  try {
    const response = await fetch(`${baseUrl}/api/checkout`, {
      method: 'POST',
      body: JSON.stringify({ productCart, customer, billing_address, return_url, customMetadata }),
    })

    if (!response.ok) {
      throw new Error(`Failed to checkout: ${response.status} ${response.statusText}`)
    }

    return await response.json()
  } catch (error) {
    console.error('Error checking out:', error)
    throw error
  }
}

'use client'

import { useState, useCallback } from 'react'
import { DodoPayments } from 'dodopayments'
import {
  getProducts,
  getProduct,
  getCustomer,
  getCustomerSubscriptions,
  getCustomerPayments,
  createCustomer,
  updateCustomer,
  checkout,
} from '@/lib/dodopayments'

interface UseBillingState {
  loading: boolean
  error: string | null
}

export const useBilling = ({ baseUrl }: { baseUrl?: string }) => {
  const [state, setState] = useState<UseBillingState>({
    loading: false,
    error: null,
  })

  const setLoading = useCallback((loading: boolean) => {
    setState(prev => ({ ...prev, loading }))
  }, [])

  const setError = useCallback((error: string | null) => {
    setState(prev => ({ ...prev, error }))
  }, [])

  const handleAsyncOperation = useCallback(async <T>(
    operation: () => Promise<T>,
    operationName: string
  ): Promise<T> => {
    try {
      setLoading(true)
      setError(null)
      const result = await operation()
      return result
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : `Failed to ${operationName}`
      setError(errorMessage)
      throw error
    } finally {
      setLoading(false)
    }
  }, [])

  const fetchProducts = useCallback(async () => {
    return handleAsyncOperation(() => getProducts({ baseUrl }), 'fetch products')
  }, [handleAsyncOperation])

  const fetchProduct = useCallback(async (product_id: string) => {
    return handleAsyncOperation(() => getProduct({ baseUrl, product_id }), 'fetch product')
  }, [handleAsyncOperation])

  const fetchCustomer = useCallback(async (customer_id: string) => {
    return handleAsyncOperation(() => getCustomer({ baseUrl, customer_id }), 'fetch customer')
  }, [handleAsyncOperation])

  const fetchCustomerSubscriptions = useCallback(async (customer_id: string) => {
    return handleAsyncOperation(() => getCustomerSubscriptions({ baseUrl, customer_id }), 'fetch customer subscriptions')
  }, [handleAsyncOperation])

  const fetchCustomerPayments = useCallback(async (customer_id: string) => {
    return handleAsyncOperation(() => getCustomerPayments({ baseUrl, customer_id }), 'fetch customer payments')
  }, [handleAsyncOperation])

  const createNewCustomer = useCallback(async (customer: DodoPayments.Customers.CustomerCreateParams) => {
    return handleAsyncOperation(() => createCustomer({ baseUrl, customer }), 'create customer')
  }, [handleAsyncOperation])

  const updateExistingCustomer = useCallback(async (
    customer_id: string,
    customer: DodoPayments.Customers.CustomerUpdateParams
  ) => {
    return handleAsyncOperation(() => updateCustomer({ baseUrl, customer_id, customer }), 'update customer')
  }, [handleAsyncOperation])

  const createCheckout = useCallback(async (
    productCart: Array<{ product_id: string; quantity: number; amount?: number }>,
    customer: DodoPayments.Payments.CustomerRequest,
    billing_address: DodoPayments.Payments.BillingAddress,
    return_url: string,
    customMetadata?: Record<string, string>
  ) => {
    return handleAsyncOperation(
      () => checkout({ baseUrl, productCart, customer, billing_address, return_url, customMetadata }),
      'create checkout'
    )
  }, [handleAsyncOperation])

  const clearError = useCallback(() => {
    setError(null)
  }, [setError])

  return {
    // State
    loading: state.loading,
    error: state.error,

    // Actions
    clearError,

    // Product operations
    fetchProducts,
    fetchProduct,

    // Customer operations
    fetchCustomer,
    fetchCustomerSubscriptions,
    fetchCustomerPayments,
    createNewCustomer,
    updateExistingCustomer,

    // Checkout operations
    createCheckout,
  }
}

import { getDodoPaymentsClient } from "@/lib/dodopayments";
import { DodoPayments } from "dodopayments";
import { NextResponse } from "next/server";
import { z } from "zod";

const productCartItemSchema = z.object({
    product_id: z.string().min(1, "Product ID is required"),
    quantity: z.number().int().min(1, "Quantity must be at least 1"),
    amount: z.number().int().min(0).optional(),
});

const attachExistingCustomerSchema = z.object({
    customer_id: z.string().min(1, "Customer ID is required"),
});

const newCustomerSchema = z.object({
    email: z.string().email("Invalid email format"),
    name: z.string().min(1, "Name is required"),
    phone_number: z.string().optional().nullable(),
    create_new_customer: z.boolean().optional(),
});

const customerSchema = z.union([attachExistingCustomerSchema, newCustomerSchema]);

const billingAddressSchema = z.object({
    city: z.string().min(1, "City is required"),
    country: z.string().regex(/^[A-Z]{2}$/, "Country must be a 2-letter uppercase ISO code"),
    state: z.string().min(1, "State is required"),
    street: z.string().min(1, "Street address is required"),
    zipcode: z.string().min(1, "Zipcode is required"),
});

const checkoutSessionSchema = z.object({
    productCart: z.array(productCartItemSchema).min(1, "At least one product is required"),
    customer: customerSchema,
    billing_address: billingAddressSchema,
    return_url: z.string().url("Return URL must be a valid URL"),
    customMetadata: z.record(z.string(), z.string()).optional(),
});

export async function POST(request: Request) {
    try {
        const body = await request.json();

        const validationResult = checkoutSessionSchema.safeParse(body);
        if (!validationResult.success) {
            return NextResponse.json(
                {
                    error: "Validation failed",
                    details: validationResult.error.issues.map(issue => ({
                        field: issue.path.join('.'),
                        message: issue.message
                    }))
                },
                { status: 400 }
            );
        }

        const { productCart, customer, billing_address, return_url, customMetadata } = validationResult.data;

        const session = await getDodoPaymentsClient().checkoutSessions.create({
            product_cart: productCart,
            customer: customer,
            billing_address: billing_address as DodoPayments.Payments.BillingAddress,
            return_url: return_url,
            metadata: customMetadata,
        });

        return NextResponse.json(session);
    } catch (error) {
        console.error('Error in checkout POST handler:', error);
        return NextResponse.json(
            { error: 'Internal server error' },
            { status: 500 }
        );
    }
}

import { getDodoPaymentsClient } from "@/lib/dodopayments";
import { NextResponse } from "next/server";
import { z } from "zod";

const paymentQuerySchema = z.object({
    customer_id: z.string().min(1, "Customer ID is required"),
});

export async function GET(request: Request) {
    try {
        const url = new URL(request.url);       
        const customer_id = url.searchParams.get('customer_id');

        const validationResult = paymentQuerySchema.safeParse({ customer_id });

        if (!validationResult.success) {
            return NextResponse.json(
                { error: validationResult.error.issues[0].message },
                { status: 400 }
            );
        }

        const validatedParams = validationResult.data;

        const payments = await getDodoPaymentsClient().payments.list({
            customer_id: validatedParams.customer_id,
        });

        return NextResponse.json(payments);
    } catch (error) {
        console.error('Error fetching customer payments:', error);
        return NextResponse.json({ error: 'Failed to fetch customer payments' }, { status: 500 });
    }
}

import { getDodoPaymentsClient } from "@/lib/dodopayments";
import { NextResponse } from "next/server";
import { z } from "zod";

const customerIdSchema = z.object({
    customer_id: z.string().min(1, "Customer ID is required"),
});

const createCustomerSchema = z.object({
    email: z.string().email("Invalid email format"),
    name: z.string().min(1, "Name is required"),
    phone_number: z.string().optional().nullable(),
});

const updateCustomerSchema = z.object({
    name: z.string().optional().nullable(),
    phone_number: z.string().optional().nullable(),
});

export async function GET(request: Request) {
    try {
        const url = new URL(request.url);
        const queryParams = {
            customer_id: url.searchParams.get('customer_id'),
        };
        const validationResult = customerIdSchema.safeParse(queryParams);

        if (!validationResult.success) {
            return NextResponse.json(
                { error: validationResult.error.issues[0].message },
                { status: 400 }
            );
        }

        const { customer_id } = validationResult.data;
        const customer = await getDodoPaymentsClient().customers.retrieve(customer_id);
        return NextResponse.json(customer);
    } catch (error) {
        console.error('Error retrieving customer:', error);
        return NextResponse.json(
            { error: 'Failed to retrieve customer' },
            { status: 500 }
        );
    }
}

export async function POST(request: Request) {
    try {
        const body = await request.json();

        const validationResult = createCustomerSchema.safeParse(body);
        if (!validationResult.success) {
            return NextResponse.json(
                { error: validationResult.error.issues[0].message },
                { status: 400 }
            );
        }

        const customer = await getDodoPaymentsClient().customers.create(validationResult.data);
        return NextResponse.json(customer);
    } catch (error) {
        console.error('Error creating customer:', error);
        return NextResponse.json(
            { error: 'Failed to create customer' },
            { status: 500 }
        );
    }
}

export async function PUT(request: Request) {
    try {
        const url = new URL(request.url);
        const customer_id = url.searchParams.get('customer_id');
        const body = await request.json();

        const customerIdValidation = customerIdSchema.safeParse({ customer_id });
        if (!customerIdValidation.success) {
            return NextResponse.json(
                { error: customerIdValidation.error.issues[0].message },
                { status: 400 }
            );
        }

        const updateValidation = updateCustomerSchema.safeParse(body);
        if (!updateValidation.success) {
            return NextResponse.json(
                { error: updateValidation.error.issues[0].message },
                { status: 400 }
            );
        }

        const { customer_id: validCustomerId } = customerIdValidation.data;
        const customer = await getDodoPaymentsClient().customers.update(validCustomerId, updateValidation.data);
        return NextResponse.json(customer);
    } catch (error) {
        console.error('Error updating customer:', error);
        return NextResponse.json(
            { error: 'Failed to update customer' },
            { status: 500 }
        );
    }
}

