import { DodoPayments } from "dodopayments"

export type Product = DodoPayments.Product
export type Customer = DodoPayments.Customers.Customer
export type Subscription = DodoPayments.Subscriptions.Subscription
export type Payment = DodoPayments.Payments.Payment


export const getProducts = async ({
  baseUrl,
}: {
  baseUrl: string
}): Promise<Product[]> => {
  const response = await fetch(`${baseUrl}/products`)
  if (!response.ok) {
    throw new Error(`Failed to fetch products: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const getProduct = async ({
  baseUrl,
  product_id,
}: {
  baseUrl: string
  product_id: string
}): Promise<Product> => {
  const response = await fetch(`${baseUrl}/product?product_id=${product_id}`)
  if (!response.ok) {
    throw new Error(`Failed to fetch product: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const getCustomer = async ({
  baseUrl,
  customer_id,
}: {
  baseUrl: string
  customer_id: string
}): Promise<Customer> => {
  const response = await fetch(`${baseUrl}/customer?customer_id=${customer_id}`)
  if (!response.ok) {
    throw new Error(`Failed to fetch customer: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const getCustomerSubscriptions = async ({
  baseUrl,
  customer_id,
}: {
  baseUrl: string
  customer_id: string
}): Promise<Subscription[]> => {
  const response = await fetch(`${baseUrl}/customer/subscriptions?customer_id=${customer_id}`)
  if (!response.ok) {
    throw new Error(`Failed to fetch subscriptions: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const getCustomerPayments = async ({
  baseUrl,
  customer_id,
}: {
  baseUrl: string
  customer_id: string
}): Promise<Payment[]> => {
  const response = await fetch(`${baseUrl}/customer/payments?customer_id=${customer_id}`)
  if (!response.ok) {
    throw new Error(`Failed to fetch payments: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const createCustomer = async ({
  baseUrl,
  customer,
}: {
  baseUrl: string
  customer: DodoPayments.Customers.CustomerCreateParams
}): Promise<Customer> => {
  const response = await fetch(`${baseUrl}/customer`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(customer),
  })
  if (!response.ok) {
    throw new Error(`Failed to create customer: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const updateCustomer = async ({
  baseUrl,
  customer_id,
  customer,
}: {
  baseUrl: string
  customer_id: string
  customer: DodoPayments.Customers.CustomerUpdateParams
}): Promise<Customer> => {
  const response = await fetch(`${baseUrl}/customer?customer_id=${customer_id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(customer),
  })
  if (!response.ok) {
    throw new Error(`Failed to update customer: ${response.status} ${response.statusText}`)
  }
  return response.json()
}


export const checkout = async ({
  baseUrl,
  productCart,
  customer,
  billing_address,
  return_url,
  customMetadata,
}: {
  baseUrl: string
  productCart: Array<{ product_id: string; quantity: number; amount?: number }>
  customer: DodoPayments.Payments.CustomerRequest
  billing_address: DodoPayments.Payments.BillingAddress
  return_url: string
  customMetadata?: Record<string, string>
}): Promise<{ checkout_url: string }> => {
  const response = await fetch(`${baseUrl}/checkout`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ productCart, customer, billing_address, return_url, customMetadata }),
  })
  if (!response.ok) {
    throw new Error(`Failed to checkout: ${response.status} ${response.statusText}`)
  }
  return response.json()
}

import { useState, useCallback } from "react";
import { DodoPayments } from "dodopayments";
import {
  getProducts,
  getProduct,
  getCustomer,
  getCustomerSubscriptions,
  getCustomerPayments,
  createCustomer,
  updateCustomer,
  checkout,
} from "../../lib/dodopayments";

interface UseBillingState {
  loading: boolean;
  error: string | null;
}

export function useBilling({ baseUrl }: { baseUrl?: string } = {}) {
  const resolvedBaseUrl =
    baseUrl ?? process.env.VITE_BASE_URL ?? "http://localhost:8080";

  const [state, setState] = useState<UseBillingState>({
    loading: false,
    error: null,
  });

  const [products, setProducts] = useState<DodoPayments.Product[]>([]);
  const [customer, setCustomer] =
    useState<DodoPayments.Customers.Customer | null>(null);

  const setLoading = useCallback((loading: boolean) => {
    setState((prev) => ({ ...prev, loading }));
  }, []);

  const setError = useCallback((error: string | null) => {
    setState((prev) => ({ ...prev, error }));
  }, []);

  const handleAsyncOperation = useCallback(
    async <T>(
      operation: () => Promise<T>,
      operationName: string
    ): Promise<T> => {
      try {
        setLoading(true);
        setError(null);
        const result = await operation();
        return result;
      } catch (error) {
        const errorMessage =
          error instanceof Error ? error.message : `Failed to ${operationName}`;
        setError(errorMessage);
        throw error;
      } finally {
        setLoading(false);
      }
    },
    [setLoading, setError]
  );

  const fetchProducts = useCallback(async (): Promise<
    DodoPayments.Product[]
  > => {
    const result = await handleAsyncOperation(
      () => getProducts({ baseUrl: resolvedBaseUrl }),
      "fetch products"
    );
    setProducts(result);
    return result;
  }, [handleAsyncOperation, resolvedBaseUrl]);

  const fetchProduct = useCallback(
    async (product_id: string): Promise<DodoPayments.Product> => {
      return handleAsyncOperation(
        () => getProduct({ baseUrl: resolvedBaseUrl, product_id }),
        "fetch product"
      );
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const fetchCustomer = useCallback(
    async (customer_id: string): Promise<DodoPayments.Customers.Customer> => {
      const result = await handleAsyncOperation(
        () => getCustomer({ baseUrl: resolvedBaseUrl, customer_id }),
        "fetch customer"
      );
      setCustomer(result);
      return result;
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const fetchCustomerSubscriptions = useCallback(
    async (
      customer_id: string
    ): Promise<DodoPayments.Subscriptions.Subscription[]> => {
      return handleAsyncOperation(
        () =>
          getCustomerSubscriptions({ baseUrl: resolvedBaseUrl, customer_id }),
        "fetch customer subscriptions"
      );
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const fetchCustomerPayments = useCallback(
    async (customer_id: string): Promise<DodoPayments.Payments.Payment[]> => {
      return handleAsyncOperation(
        () => getCustomerPayments({ baseUrl: resolvedBaseUrl, customer_id }),
        "fetch customer payments"
      );
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const createNewCustomer = useCallback(
    async (
      newCustomer: DodoPayments.Customers.CustomerCreateParams
    ): Promise<DodoPayments.Customers.Customer> => {
      const result = await handleAsyncOperation(
        () =>
          createCustomer({ baseUrl: resolvedBaseUrl, customer: newCustomer }),
        "create customer"
      );
      setCustomer(result);
      return result;
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const updateExistingCustomer = useCallback(
    async (
      customer_id: string,
      updatedCustomer: DodoPayments.Customers.CustomerUpdateParams
    ): Promise<DodoPayments.Customers.Customer> => {
      const result = await handleAsyncOperation(
        () =>
          updateCustomer({
            baseUrl: resolvedBaseUrl,
            customer_id,
            customer: updatedCustomer,
          }),
        "update customer"
      );
      setCustomer(result);
      return result;
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const createCheckout = useCallback(
    async (
      productCart: Array<{
        product_id: string;
        quantity: number;
        amount?: number;
      }>,
      customer: DodoPayments.Payments.CustomerRequest,
      billing_address: DodoPayments.Payments.BillingAddress,
      return_url: string,
      customMetadata?: Record<string, string>
    ) => {
      return handleAsyncOperation(
        () =>
          checkout({
            baseUrl: resolvedBaseUrl,
            productCart,
            customer,
            billing_address,
            return_url,
            customMetadata,
          }),
        "create checkout"
      );
    },
    [handleAsyncOperation, resolvedBaseUrl]
  );

  const clearError = useCallback(() => {
    setError(null);
  }, [setError]);

  return {
    loading: state.loading,
    error: state.error,
    products,
    customer,
    clearError,
    fetchProducts,
    fetchProduct,
    fetchCustomer,
    fetchCustomerSubscriptions,
    fetchCustomerPayments,
    createNewCustomer,
    updateExistingCustomer,
    createCheckout,
  };
}
